function dijkstra(graph, start) {
  const dist = {};
  const visited = new Set();
  const pq = new MinPriorityQueue();
  for (let node in graph) dist[node] = Infinity;
  dist[start] = 0;
  pq.enqueue(start, 0);
  while (!pq.isEmpty()) {
    const { element: node } = pq.dequeue();
    if (visited.has(node)) continue;
    visited.add(node);
    for (let [nei, weight] of graph[node]) {
      if (dist[node] + weight < dist[nei]) {
        dist[nei] = dist[node] + weight;
        pq.enqueue(nei, dist[nei]);
      }
    }
  }
  return dist;
}
